window.onload=()=>{document.querySelectorAll("[url][fn]").forEach(e=>{const t=e.attributes;e[t.fn.value]=()=>{fetch(t.url.value).then(e=>e.json()).then(e=>{e.change.forEach((t,r)=>{"string"==typeof e.target?document.querySelector(e.target)[t]=e.value[r]:document.querySelector(e.target[r])[t]=e.value[r]})})}})};
